def chegaAFim(termo):
    espaco=10
    termos=[termo]
    while True:
        termo=proximoTermo(termo)
        if(len(termo)>=espaco):
            termo=termo[:espaco]
        if(termo not in termos):
            termos.append(termo)
        else:
            termos.append(termo)
            break
    return(termos)

def proximoTermo(termo):
    resultado=[]
    if(len(termo)<=1):
        return(termo)
    for indice in range(len(termo)-1):
        digito1=termo[indice]
        digito2=termo[indice+1]
        soma=int(digito1)+int(digito2)
        bidigito=str(soma)
        resultado.append(bidigito)
    if(not(resultado)):
        resultado=[termo]
    termo=''.join(resultado)
    return(termo)

